import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  title = 'musicPage';
  public slides = [
    { src: 'https://media.istockphoto.com/id/1660911398/photo/aerial-view-of-hong-kong-financial-district-and-business-centers-at-victoria-island.jpg?s=1024x1024&w=is&k=20&c=mBXZW6l0G6SfF1VOlvGjMmvNes1wqXdg2cYhl-_J8Cw=' },
    { src: 'https://image2.com' },
    { src: 'https://image3.com' },
    { src: 'https://image4.com' },
  ];
}
<mat-toolbar color="primary">
  <mat-icon>queue_music</mat-icon>
  <span></span>
</mat-toolbar>

<div class="flex-container">
  <div class="flex-item-left">
    <app-spotify-player></app-spotify-player>
  </div>
  <div class="flex-item-right">
    <app-about-section></app-about-section>
  </div>
</div>

<div class="container">
  <app-carousel [slides]="slides"></app-carousel>
</div>
